import * as t from "io-ts";

/**
 * If supplied, only transaction request transaction state changes from the provided
 * list will trigger notifications. If not provided, all transaction request
 * transaction state changes will trigger notifications.
 */
export const TransactionState = t.keyof(
  {
    initialized: 1,
    pendingSignature: 1,
    eddsaPendingCommitment: 1,
    eddsaPendingRShare: 1,
    eddsaPendingGShare: 1,
    ecdsaMPCv2Round1: 1,
    ecdsaMPCv2Round2: 1,
    ecdsaMPCv2Round3: 1,
    readyToCombineShares: 1,
    signed: 1,
    held: 1,
    delivered: 1,
    invalidSignature: 1,
    rejected: 1,
  },
  "TransactionState",
);
export type TransactionState = t.TypeOf<typeof TransactionState>;
